import React from 'react';
import {ToggleComponent, withStorageListener} from "./withStorageListener";

function StorageChangeAlert({show} : ToggleComponent) {
    if(show) {
        return <p>Hubo cambios sobre los todos en otra pestaña de tu navegador. ¡Por favor actualiza tu pestaña!</p>;
    }

    return <></>;
}

const StorageChangeAlertWithListener = withStorageListener(StorageChangeAlert);

export default StorageChangeAlertWithListener;
