import './TodoContainer.css';
import React, {ReactNode} from "react";
import {Todo} from "../Todo/Todo";

function LoadingList() {
  return <p>Loading ...</p>;
}

function ErrorList() {
  return <p>Error al cargar la información de la aplicación</p>;
}

interface EmptyListType {
  searchPhrase : string,
}

function EmptyList({searchPhrase} : EmptyListType) {
  return <p> No hay resultados para la busqueda {searchPhrase} </p>
}

interface TodoListType {
  loading :boolean,
  error :boolean,
  todos :Todo[],
  onError : () => ReactNode,
  onLoading : () => ReactNode,
  onEmpty : () => ReactNode,
  render : (todo : Todo) => ReactNode,
}

function TodoList({loading, error, todos, onError, onLoading, render, onEmpty} : TodoListType) {
  return (
    <ul className='todo_container'>
      {error && onError()}
      {loading && !error && onLoading()}
      {!loading && !error && todos.length === 0 && onEmpty()}
      {!loading && !error && todos.map(render)}
    </ul>
  );
}

export {TodoList, ErrorList, LoadingList, EmptyList}
