import React from 'react';
import {ToggleComponent, withStorageListener} from "./withStorageListener";

interface Reloader {
    sincronizeTodos: React.Dispatch<React.SetStateAction<boolean>>;
}

type ToggleableWithAction = ToggleComponent & Reloader;
function StorageChangeAlert({show, toggle, sincronizeTodos} : ToggleableWithAction) {
    if(!show)
        return <></>;

    const onClickAction = () => {
        toggle(false)
        sincronizeTodos(false);
        console.log('se desactualizaron los todos');
    };

    return (

        <>
            <p>Hubo cambios sobre los todos en otra pestaña de tu navegador. ¡Por favor actualiza tu pestaña!</p>
            <button
                onClick={onClickAction}
            >
                Actualizar esta pestaña
            </button>
        </>
    );
}

const StorageChangeAlertWithListener = withStorageListener(StorageChangeAlert);

export default StorageChangeAlertWithListener;

export type {ToggleableWithAction, Reloader};
