import React, {ReactElement} from "react";
import {localStorageKey} from "../Storage/useLocalStorage";
import {Reloader, ToggleableWithAction} from "./StorageChangeAlert";

interface ToggleComponent {
    show : boolean,
    toggle : React.Dispatch<React.SetStateAction<boolean>>,
}

function withStorageListener(Component : (props : ToggleableWithAction) => ReactElement) {
    return function ComponentWithListener(props : Reloader) {
        const [storageChange, setStorageChange] = React.useState(false);

        document.addEventListener('storage', (event : Event) => {
            if((event as StorageEvent).key === localStorageKey){
                setStorageChange(true);
            }
        });

        return (
            <Component
                show={storageChange}
                toggle={setStorageChange}
                saveTodos={props.saveTodos}
            />
        );
    }
}

export {withStorageListener};

export type {ToggleComponent};
