import React, {ReactElement} from "react";
import {localStorageKey} from "../Storage/useLocalStorage";

interface ToggleComponent {
    show : boolean,
    toggle : React.Dispatch<React.SetStateAction<boolean>>,
}

function withStorageListener(Component : (props : ToggleComponent) => ReactElement) {
    return function ComponentWithListener() {
        const [storageChange, setStorageChange] = React.useState(true);

        document.addEventListener('storage', (event: LocalStorageEvent) => {
            if(event.key === localStorageKey){
                setStorageChange(true);
            }
        })

        return (
            <Component
                show={storageChange}
                toggle={setStorageChange}
            />
        );
    }
}

export {withStorageListener};

export type {ToggleComponent};
